        private Dictionary<short, string> Gifts = new Dictionary<short, string>()
        {
            [1] = "Маг - человек получил от судьбы дар виртуозного владения словом, талант примирять враждующие стороны, быть посредником в спорах, улаживать конфликты, а также способность соединять и в себе, и в своём окружении энергии четырёх стихий. Человек с картой «Маг» в 8. позиции умеет грамотно складывать слова, как в своей устной речи, так и в письме, а его высказывания будут логичными, ясными и красивыми. К дарам, доставшимся такому человеку, можно также отнести лёгкость получения любой информации, ловкость в завязывании новых контактов с ровесниками и плодотворное сотрудничество, как с ними, так и со своими братьями и сестрами.\r\n\r\n",
            [2] = "Жрица - человек получил в дар способность хранить тайны, возможность действовать по собственному усмотрению, интуитивное понимание других людей и прекрасный контакт со всеми природными стихиями. Этот человек настоящее «дитя природы», опекун, всегда готовый прийти на помощь тем, кто в нем нуждается. Дополнительно человек получил немалый талант в сфере магических искусств, и даже если он не пользуется никакой конкретной практикой предсказания будущего типа Таро, рун или астрологии, он может заранее предвидеть, что должно произойти и как в дальнейшем будут обстоять дела. Также легко дается такому человеку всё, что связанно непосредственно с природой — уход за цветами, животноводство, садоводство и т.д.\r\n\r\n",
            [3] = "Императрица - дары связаны с чувством прекрасного и эстетикой. Соответственно, люди имеют идеальный вкус, знания о том, как сделать жизнь богаче и ярче. Такие люди из любого, даже самого убогого пространства могут сотворить что-то исключительно прекрасное и уютное. Умеют также безошибочно определить, что к чему подходит, кому что пойдёт, как нужно вести домашнее хозяйство, как принимать гостей, как одеваться, чтобы отлично выглядеть. Такие люди являются знатоками искусства в любом его проявлении и при этом в качестве высшего дара получают еще и красивую внешность, которая упрощает жизнь, ведь недаром сказано: «Красивому можно больше».\r\n\r\n",
            [4] = "Император - человек получил в дар умение принимать решения, администрировать и управлять, а также исключительную работоспособность. Не существует такой работы, с которой человек не смог бы справится. Дополнительным подарком является редкий, удивительный талант чувствовать, что будет идти легко и прибыльно, а что не принесёт ни дохода, ни удовольствия. Эти люди часто бывают властными и требовательными, но ни от кого не требуют больше, чем требовали бы от себя и хорошо понимают, что власть - это, в первую очередь, ответственность и забота о подчинённых, а не только пользование привилегиями, которые она приносит. Люди с Императором умеют отдавать приказы и поручения, а также контролировать их исполнение.\r\n\r\n",
            [5] = "Иерофант - дары связаны, прежде всего, с умением получать и использовать свои знания в жизни на очень высоком уровне, научить других людей тому, что человек знает сам, проводить исследования, обрабатывать результаты этих исследований и доносить их до широкого круга общественности. Такие люди могут соединить воедино прошлый опыт, исторические знания и самые новые достижения науки. Благодаря этому, они могут стать духовными лидерами, действующими в рамках определённой религии, либо за её пределами. Дополнительным даром является простота завоевания авторитета, умение быть профессионалом в своём деле и известность.\r\n\r\n",
            [6] = "Влюблённые - умение делиться своей любовью в глобальном смысле этого слова. Такие люди умеют сделать выбор и принять решение независимо от того, насколько трудная сложилась ситуация. Имеют также предрасположенность к торговле, посредничеству и оказанию помощи, связанной с принятием решения. Дополнительным даром является способность налаживать сложные партнёрские отношения (как в бизнесе, так и в личной жизни).\r\n\r\n",
            [7] = "Колесница - человек получил в дар способности к карьерному росту и добиваться успехов на этом поприще. Их положительными сторонами являются динамичность, мобильность, уверенность в себе и желание добиться успехов. Частая смена мест жительства, профессии или просто путешествия не являются для таких людей проблемой, а талант настраивать любую технику значительно упрощает жизнь и ускоряет процесс роста карьеры. Дополнительными подарками судьбы являются способность увлечь своими идеями других людей, талант рационализаторства и изобретательства, новаторство в области освоения технических новшеств.\r\n\r\n",
            [8] = "Справедливость - дар исключительного чувства социальной справедливости и правовой грамотности, а также способность нахождения внутреннего равновесия в любой ситуации. Положительной чертой характера такой личности является объективный и непредвзятый подход к любому делу, что упрощает оценку имеющихся возможностей. Такие люди прекрасно реализуются в профессиях, каким-либо образом связанных с законодательством, правовыми и судебными нормами. Дополнительным даром является способность объективно оценивать ошибочные решения, дистанцироваться от них и не повторять их дважды, что позволяет успешно реализовывать свои планы и достигать поставленных целей.\r\n\r\n",
            [9] = "Отшельник - люди получили в качестве дара высокую духовность, умение находить верный жизненный путь и понимать высшие законы бытия (универсальные, космические). Такие люди обучаются не только на собственном опыте, но также используют знания, завещанные нам предками. Положительными качествами таких людей есть мудрое и доброжелательное отношение к старшим, с которыми независимо от своего возраста имеют хорошие отношения, а также способности идти по жизни путем своего Предназначения. Дополнительным талантом является умение работать над собой, духовно расти, обогащать свой внутренний мир духовными вечными ценностями. Хотя и не чураются общения с другими людьми, но всё же лучше чувствуют себя в одиночестве, которое отнюдь не мешает хорошо выполнять свою работу.\r\n",
            [10] = "Колесо Фортуны - показывает, что человек одарен незаурядным чутьем на дела прибыльные, бережному отношению к деньгам, умением их заработать и правильно использовать. Такие люди хорошо знают цену, как своей работе, так и деньгам, которые за неё получают. Умеют они отделить сферу материальную от духовной, не забывают и о мире физическом. Дополнительно такие люди прорабатывают карму, часто даже не зная, что что-то подобное существует. Во второй половине жизни они уже располагают достаточными денежными средствами и начинают заниматься благотворительностью.\r\n\r\n",
            [11] = "Сила — люди в качестве дара получили способность работать с физическим телом. Таких людей условно можно разделить на две группы:\r\nобладающие, хорошим здоровьем и большой физической силой;\r\nобладающие плохим здоровьем, слабые и даже с физическими недостатками, которые, тем не менее, ведут активный образ жизни и стараются улучшить своё физическое состояние.\r\nИменно те, кто имеют Силу, прекрасно понимают потребности человеческого тела, знают, как их удовлетворить, как поднять уровень жизнеспособности (витальности), иммунитета и физических сил.\r\n\r\n",
            [12] = "Повешенный – у человека есть дар доносить молитвы до Высших Сил, легкость вхождения в измененные состояния сознания, налаживания контакта со своим внутренним миром, Богом, иными Мирами. Такие люди прекрасно разбираются в своих и чужих эмоциях, что в огромной степени упрощает понимание, как себя, так и окружающего мира. Дар успешного взаимодействия со своим внутренним миром дает возможность пользоваться подсказками подсознания, а если понаблюдать за такими людьми, то создается впечатление, что что-то ведет их к высшей цели. Молитвы их обладают целительными и очищающими качествами. Дополнительным даром является способность поставить все с ног на голову и начать все сначала, отталкиваясь от такой не похожей на другие точки зрения.\r\n\r\n",
            [13] = "Смерть - даром является способность к трансформации, как себя, так и окружающий мир, а также талант излечивать тело и душу. Дополнительным подарком судьбы является отсутствие страха перед изменениями, вызовами судьбы и возникающими возможностями. Эти люди, в принципе, не имеют проблем с использованием своих «пяти минут», которые судьба даёт испытать каждому из нас. В жизни таких людей часто доходит дело до радикальных изменений, как в профессиональной жизни, так и в жизни личной, но это их абсолютно не беспокоит. С лёгкостью могут приносить изменения в судьбы других людей или реализоваться в экстремальных профессиях, которые требуют крепких нервов и полного контроля над собой.\r\n\r\n",
            [14] = "Умеренность - такие люди получили в дар от судьбы талант вносить равновесие в очень проблемные и натянутые отношения, умение находить выход из трудных ситуаций, привносить гармонию и спокойствие в свое окружение. Дополнительным умением является способность к нахождению золотой середины, устранению диссонансов между внутренним и внешним миром, мирному разрешению конфликтов и недоразумений, гармонизации несбалансированных эмоций. Таких людей ценят за их спокойствие, сдержанность, умение посмотреть на ситуацию со стороны, внешнюю мягкость и внутреннюю гармонию. Это прирождённые дипломаты, которые умеют найти выход в любой ситуации, дать верный совет и медленно, но уверенно двигаться к цели.\r\n\r\n",
            [15] = "Дьявол - люди одарены огромной энергией, которая может быть реализована на разных уровнях. Окружающие часто удивляются активности, экспрессии и динамизму, которые присутствуют у этих людей в полной мере. Очень часто такие люди получают целительский дар, умеют поднять настроение в любой компании и привнести позитивную энергию. Поэтому такие люди могут заниматься биоэнерготерапией, Рэйки или иного рода целительством. Дополнительно обладают такими чертами характера, которые делают их душой любой компании, отличными работниками, которые не боятся трудных заданий, людьми очаровательными, вызывающими особый интерес у противоположного пола.\r\n\r\n",
            [16] = "Башня - умение разрушать старые, изжитые, закостенелые системы и строить на их месте новые, более современные. Часто такие люди могут из ничего создать сложную структуру, которая будет в состоянии существовать долгие годы и в любых условиях. Не является для них проблемой вписаться в уже существующие структуры, если считают их перспективными, стабильными и способными выполнять поставленные задачи. Дополнительно такие люди имеют дар чувствовать время, умеют его организовать и с пользой тратить каждую минуту. Так же имеют способности в области логистики, знания которой используют как в личной, так и профессиональной сфере и благодаря, которым становятся прекрасными работниками или руководителями, без труда организовывают работу даже очень больших коллективов.\r\n\r\n",
            [17] = "Звезда - люди со Звездой имеют художественный талант, они креативны и умеют создавать произведения искусства. Благодаря этим способностям к любой работе подходят творчески и своими успехами оказывают влияние на окружающих. Дополнительно эти люди умеют вызывать у окружающих дружеские чувства и сами являются верными друзьями. Обладая такими чертами характера как чувствительность, деликатность, утончённость, могут вызвать восхищение не только своим творчеством, но и своим необычным и обаятельным поведением, изысканными манерами. Ещё одним даром судьбы является покровительство Высших Сил, которое сопутствует им в жизни. О таких людях не зря говорят: «Рождённый под счастливой звездой».\r\n\r\n",
            [18] = "Луна - человек наделён отличной интуицией, знанием человеческой психики, живым воображением, богатой фантазией и сверхчувствительностью. Помогают эти дары реализоваться в любых видах деятельности, связанных с общением и коммуникациями между людьми, умением предвидеть результаты своих действий, дальновидностью и способностью реагировать на эмоциональное состояние окружающих. Дополнительно такие люди наделены способностью налаживать контакт с подсознанием и понимать его подсказки. Иногда люди, наблюдающие со стороны, могут удивиться умению человека с «Луной» интуитивно выбирать то, что даст лучшие результаты. Так же эти люди обладают способностью заботиться о своём доме и поддерживать в нём уютную атмосферу.\r\n\r\n",
            [19] = "Солнце - человек имеет талант руководителя, лидера и поэтому без труда может занимать любые руководящие должности, где необходимы такие черты характера, как твёрдость и авторитетность. Такой человек способен вести за собой людей, давать им мотивацию к действию и убеждать в своей правоте. Он точно знает, чего хочет не только от себя, но и от других. Дополнительно такие люди получают в дар харизму, талант выступать на публике, умение убедительно и точно излагать свою точку зрения, способность аргументировать и излагать самую суть любого дела. Их несомненной положительной чертой является нежелание почивать на лаврах и постоянная работа над собственным характером, развитие имеющихся способностей и практическое их использование, а также глубинное понимание детской психики и умение быть “вожаком” для детей.\r\n\r\n",
            [20] = "Суд - эта карта показывает в качестве дара умение творить здоровую и мудрую систему ценностей, развивать позитивные отношения между людьми, а также демонстрирует открытый ум со сверхспособностями к анализу и синтезу, что помогает реально оценивать ситуацию, делать выводы и применять полученный опыт на практике. Дополнительно такие люди очень семейные, и при этом семьёй считают не только близких и родных, но и сотрудников, соседей, знакомых и даже свой народ или все человечество. Многие из них, часто даже сами того не зная, отрабатывают родовые кармические долги. Такие люди имеют позитивное мировоззрение, а депрессии им вообще не известны; они гордятся своим происхождением и никогда не поддерживают любые проявления расизма и клановости.\r\n\r\n",
            [21] = "Мир - человек с такой картой на 8. позиции в Портрете получил в дар космополитизм, толерантность, умение налаживать контакты с иностранцами, способность смотреть на мир объективно, интерес ко всему современному и отсутствие блокад и комплексов. Такой человек чувствует себя хорошо в любой среде, умеет приспосабливаться к любым условиям, способен быстро выучить язык, обычаи и культуру страны, в которой ему приходится жить. Дополнительным даром является чувство юмора, умение посмотреть на себя со стороны и талант упрощать свою и чужую жизнь. Такие люди идут по жизни с улыбкой, с интересом, ко всему, что может встретиться им в жизни, они позитивные, современные и уверенные в себе. И нет ничего удивительного в том, что они добиваются успеха в различных видах деятельности и часто получают мировое признание.\r\n\r\n",
            [22] = "Шут - человек получил в дар способность, присущую детям, когда ты с интересом и энтузиазмом берешься за новые и разнообразные дела, спонтанность, азарт, способность к нахождению нетрадиционных решений. Такой человек, поскольку доверяет своим инстинктам, сможет сделать из любой трудной работы отличную забаву и с неподдельным энтузиазмом выполнит поставленную перед ним задачу, всегда найдёт нетрадиционный выход из сложившейся ситуации, а если и воспользуется традиционным методом, то при этом обязательно внесёт в него что-то новое и решит проблему. Такие люди до конца своей жизни остаются молодыми в душе, а цинизм и скука им не присущи вообще.\r\n\r\n"
        };
        private Dictionary<short, string> Karma = new Dictionary<short, string>()
        {
            [1] = "Маг - люди должны отрабатывать кармические обязанности, налаживая и развивая большое количество позитивных контактов. Такие люди должны заботиться об отношениях с родными и ровесниками, работать над словом как устным, так и письменным, следить за тем, чтобы в коллективе сохранялась гармония. Также нужно взвешивать каждое слово и не только никого им не обижать, но и стараться всем сказать что-то приятное.",
            [2] = "Жрица - наладить хорошие отношения с матерью, заботиться о тех, кто в этом нуждается. Необходимо научиться хранить тайны и поступать по своему усмотрению. Заниматься эзотерикой (например, Таро или природной магией), охранять природу и заботиться о животных и окружающей среде всей планеты.",
            [3] = "Императрица - способом отработки кармических долгов является построение хороших отношений со своими детьми, забота о доме и создание тёплой, сердечной атмосферы между домашними, а также иметь хорошие отношения с партнёром. Этот человек должен придерживаться такого отношения к материальным ценностям, чтобы никогда не становиться их пленником, но и не пренебрегать их значением.\r\n",
            [4] = "Император - посвятить себя профессиональной деятельности и со всей ответственностью к ней относиться, бороться с ленью во всех её проявлениях и при этом построить хорошие отношения со своим партнёром. Также необходимо выработать соответствующее отношение к власти, а если судьба дала возможность быть лидером, то помнить, что его положение, в первую очередь, обязывает быть ответственным.\r\n",
            [5] = "Иерофант - человек должен стать примером для подражания и учителем.\r\nС уверенностью можно сказать, что для отработки кармических долгов необходимо не лицемерить, быть искренним в отношениях с другими людьми, получить высшее образование, выработать цельную философскую систему, личностное отношение к религии и вере, профессиональный подход к рабочим обязанностям. Необходимо также заботиться о хороших отношениях с отцом и другими авторитетными лицами.",
            [6] = "Влюблённые - отработать свои кармические долги, одаривая всех и всё космической, универсальной любовью. Такой человек должен забыть обо всех разграничениях и воспринимать всех одинаково, независимо от пола, возраста, национальности и т. д., а наибольшее внимание уделить всем видам партнёрских отношений. Не последнее место будет занимать умение принимать решения и делать выбор, а также нести за эти решения персональную ответственность.",
            [7] = "Колесница - способом отработки долгов будет стремление к успеху, полученному кристально честным путём. Такой человек должен получить признание и стать популярным как минимум в своём ближайшем окружении, для того, чтобы иметь возможность вносить позитивные изменения хотя бы в такое небольшое сообщество. Необходимо пропагандировать идею взаимопонимания, согласия, сотрудничества между разными людьми и социальными группами.",
            [8] = "Правосудие - человек, чтобы отработать свои кармические долги, должен соблюдать общечеловеческие правила и нормы, быть чувствительным к любым проявлениям несправедливости и ни в коем случае не проходить мимо таких случаев. Необходимо так вести свои дела, чтобы никто не мог обвинить в нечестности, а тем более подозревать в преступлении. В личной жизни задание человека состоит в том, чтобы выработать и сохранить внутреннее равновесие.",
            [9] = "Отшельник — человек должен все силы вложить в своё духовное развитие, научиться правильно применять свой жизненный опыт, заниматься эзотерикой, медициной или другими науками о человеке, а также выработать в себе положительное отношение к пожилым людям, а особое внимание обратить на отношения со своими бабушками и дедушками. Главные задания такого человека: служение другим людям, скромное поведение, отказ от лени, принятие того, что приносит жизнь, даже если это одиночество среди людей.\r\n",
            [10] = "Колесо Фортуны - человек должен в первую очередь сконцентрироваться на том, чтобы найти равновесие между духовным и материальным, выработать правильное отношение к деньгам, научиться их зарабатывать, чтобы материально обеспечить своих близких, помочь тем, кто в этом нуждается, и участвовать в благотворительности. Такой человек должен помнить, что душа осознанно пришла в этот мир, чтобы отработать свои кармические долги, поэтому нужно прислушиваться к её голосу.",
            [11] = "Сила - выработать соответствующее отношение к своему телу, которое является «храмом для души», здоровью, иммунитету и физической форме. Также в полной мере использовать все возникающие возможности, не пренебрегать и не стыдиться любой работы, даже физической, и развивать способности и таланты, данные судьбой. Такой человек должен помнить о том, что если за что-то взялся добровольно, что-то кому-то пообещал, то обязан выполнить независимо от цены, которую придется за это заплатить.\r\n",
            [12] = "Повешенный - человек для отработки кармических долгов должен погрузиться в свой внутренний мир, понять себя, свои эмоции, но не навязывать их окружающим, а справляться с ними самостоятельно и не строить из себя жертву. Такой человек должен молиться и просить Высшие Силы благословить не только себя, но и других людей. При этом не ждать ничего взамен, так как человек в прошлой жизни был слишком пассивным по отношению к себе и другим людям и теперь должен брать инициативу в свои руки, работать и поощрять других делать то же самое.\r\n",
            [13] = "Смерть - человек для отработки своих долгов из предыдущих воплощений должен выработать умение приспосабливаться к постоянным изменениям, в которых нуждается. Способность к трансформациям, очищению от того, что себя изжило и неактуально, отмежевания от прошлого - самые главные и эффективные способы ликвидации кармических задолженностей в этом случае. Человек должен научиться работать в экстремальных условиях, спасать жизни, оказывать поддержку отчаявшимся и несчастным.\r\n",
            [14] = "Умеренность - человек должен быть очень внимательным в отношениях с самым близким окружением, соседями и знакомыми, помогать и поддерживать их, а в случае конфликтов, выступать в роли посредника и миротворца. Задание такого человека в этой жизни заключается в том, чтобы установить гармонию между внешним и внутренним миром. Необходимо научиться контролировать свои эмоции, выработать умение находить золотую середину в любой ситуации, независимо от обстоятельств.",
            [15] = "Дьявол — человек должен научиться справляться со своей энергией, эффективно ею пользоваться, работать на пользу обиженных и беззащитных созданий. Такой человек должен выработать в себе соответствующее отношение к сексу, эротике и привнести в свою интимную жизнь духовность, не забывая и о физическом удовольствии. Важную роль играет также честность, объективность, верность, умение не поддаваться соблазнам и держать дистанцию с окружением.\r\n",
            [16] = "Башня - человек, чтобы отработать свои кармические долги, должен научиться строить и сохранять отношения с людьми, начиная со своей семьи и заканчивая организациями и государственными учреждениями. Такой человек должен уметь заботиться о том, чтобы не поддаваться соблазну навязывать своё мнение другим людям, рушить все, что не соответствует его взглядам, руководствоваться минутными капризами и обижаться на тех, кто не согласен с его мнением.\r\n",
            [17] = "Звезда - человек может отрабатывать свои кармические долги благодаря культивированию дружеских уз, лояльности и верности своим друзьям, доброжелательному отношению к миру, умению разбираться в чувствах своих и чужих, отказу от цинизма. Такой человек не должен допускать скуку в свою жизнь и полностью использовать имеющиеся таланты, в основном в художественной сфере. Человек также не должен быть равнодушным к красоте, добру, бескорыстию, творчеству других людей и высшим ценностям.\r\n",
            [18] = "Луна - обладатель Луны на 15. позиции, чтобы отработать кармические долги должен иметь хороший контакт со своим подсознанием, сотрудничать со своей интуицией и прислушиваться к её подсказкам. Такой человек должен контролировать своё воображение и не поддаваться иллюзиям, чтобы иметь возможность правильно оценивать реальность. Также необходимо заботиться о своём доме, семье, народе, родине, быть верным своим взглядам, никогда не забывать о принятых на себя обязанностях, но выполнять их, пользуясь мягкой женской энергией, независимо от пола в нынешнем воплощении.\r\n",
            [19] = "Солнце - человек должен избегать тенденций быть руководителем и навязывать своё руководство другим людям. Ну а если все же случится так, что человек займёт руководящую должность, то не забывать, что лежит на нём огромная ответственность и к нему значительно больше требований, чем к другим людям в такой же ситуации, но с другой картой способа проработки кармы. Человек должен указывать дорогу другим людям, оставаясь при этом в стороне, быть скромным, сохранять спокойствие духа и своё мнение, независимо от ситуации, на которую имеет влияние.\r\n",
            [20] = "Суд - чтобы отработать кармические долги, человек с такой картой на 15. позиции должен посвятить много времени и труда семье, и не только домашним или самым близким, родным, но и дальним родственникам, заботиться о том, чтобы между ними сохранялись хорошие отношения, культивировать семейные традиции, помнить о своих корнях, а также работать над цельной системой ценностей людей и мировоззрением. Человек должен помнить, что его душа пришла в этот мир специально, чтобы отработать всякие родовые кармические долги, поэтому должен прислушиваться к её голосу и следовать ему.\r\n",
            [21] = "Мир - заданием человека с этой картой в Портрете является выработка в себе высокого уровня толерантности, понимания и терпеливости к людям, независимо от их происхождения, образования, расы, национальности и культуры. Человек должен налаживать и поддерживать как можно большее количество контактов на международном уровне, перебрасывать мосты через границы, уважать традиции и обычаи других людей, быть открытым для современных трендов и тенденций, вводить технические рационализации и усовершенствования, а также избавиться от любого вида фобий, блокад и предубеждений.\r\n",
            [22] = "Шут - способ проработки кармических обязанностей в случае человека состоит в том, чтобы родить и воспитать детей, работать с детьми, заботиться об их благополучии (в смысле личном и социальном). Такой человек должен не отступать перед каждым новым вызовом судьбы, быть открытым для новых дел, явлений, задач, а также культивировать в себе энтузиазм, присущий детям и бороться со стереотипами и рутинным поведением."
        };
        private Dictionary<short, string> MyDestiny = new Dictionary<short, string>()
        {
            [1] = "человек должен наполнять своё окружение гармонией и энергетикой, работать над словом (как устным, так и письменным), получать знания и необходимую информацию, обращать внимание на отношения с братьями и сестрами, а также с ровесниками. Необходимо принять себя, прежде всего, таким, какой ты есть, а уже потом работать над своим развитием. Такой человек допускает большую ошибку, если не желает учиться, не использует свои ораторские способности и приносит дисгармонию в отношения с близкими людьми. Миссия такого человека состоит в том, чтобы помогать добрым и мудрым словом, приносить гармонию в мир и снимать энергетическое напряжение. Разговор с таким человеком должен оставлять неизгладимый след в памяти. Многие люди с правильно проработанным Магом становятся писателями, ораторами или мотивационными спикерами.",
            [2] = "человек должен направлять свою активность на дела, связанные с природой, земледелием, естественными науками. Нельзя оставлять так же в стороне и отношения с матерью и другими женщинами. Стоит обратить внимание на тайные знания, оккультные науки и стараться познать внутренний мир. На более высоком уровне Жрица реализуется через материнство в самом широком смысле этого слова, а точнее через заботу о других людях, опеку тех, кто в этом нуждается, оказание поддержки молодым и помощь тем, кто потерялся в жизни. Именно по этой причине женщинам легче реализоваться, чем мужчинам. Человек делает большую ошибку, если не обращает внимания на окружающую природу, не хочет строить правильных отношений с матерью, не признаёт тайные знания и не может поделиться своим теплом, не поддерживает и не пытается понять других людей. Миссия такого человека состоит в том, чтобы, в первую очередь, заботиться о своём окружении, начиная с матери, своих соседей, знакомых и даже чужих людей и заканчивая заботой о природе и охраной прав животных. Многие люди со Жрицей становятся замечательными специалистами по Таро, экологами или учителями тайных знаний.",
            [3] = "миссией человека является забота о красоте и эстетике, процветании и соответствующем материальном достатке своём и своих близких, а также о правильном распределении сил между душевной и материальной сферой. На более высоком уровне указывает на то, что человек должен стать «матерью» в широком смысле этого слова (родить ребёнка, стать автором литературного произведения, изобрести машину...). Человек делает большую ошибку, если не интересуются искусством, не заботится о своём внешнем виде и своем окружении, а так же когда либо игнорирует, либо слишком сильно концентрируется на делах материальных. Проблемы приносит и недостаточное понимание необходимости «рождения» чего-либо, что чаще случается с мужчинами. Миссия такого человека состоит в том, чтобы принести в этот мир красоту, повлиять на развитие культуры и искусства, подать пример другим, как можно примирить между собой сферы священные и мирские, духовные и материальные, богатство и любовь ко всему красивому и эстетичному. Многие люди становятся дизайнерами помещений, ювелирами, обладателями галерей или книжных магазинов.",
            [4] = "самой важной сферой деятельности должна стать профессиональная деятельность, так как именно в ней сможет реализоваться человек. Человек должен проработать своё отношение к власти, к мужчинам как таковым. Человек совершает большую ошибку, если уходит от профессиональной деятельности, либо питает к ней негативное отношение, а так же когда слепо стремится к власти, не понимая, что, прежде всего, необходимо будет нести ответственность, как за свои решения, так и за подчинённых. Женщины с Императором должны принять, как аксиому тот факт, что одна из их задач - выйти замуж и научиться жить со своим партнёром. Миссия таких людей заключается в том, что должны оставить свой след на земле благодаря своей профессиональной деятельности, а также реализоваться как хорошие хозяева в любой ситуации и сфере жизни. Многие люди становятся известными бизнесменами, директорами больших фирм, отличными губернаторами или администраторами.",
            [5] = "человек должен заниматься научными исследованиями, расширением и углублением своих познаний, получить хорошее образование либо посвятить себя религии, изучению древних культур и обычаев, культивированию традиций своего рода, уважая при этом и традиции других людей. Также необходимо быть толерантным и научиться понимать людей. Аркан напоминает и о том, что нужно наладить отношения с отцом и своими детьми, как родными, так и теми, за которых взял ответственность (например, учениками). Человек совершает большую ошибку, если легкомысленно относится к учёбе либо не хочет поделиться своими знаниями, осмеивает иные религии, обычаи и традиции. Необходимо следить за тем, чтобы ненароком не стать нетолерантным невеждой или зазнавшимся умником. Миссия человека заключается в том, чтобы наставлять людей на путь истинный, быть учителем в разных сферах жизни, стражем знаний и традиций, отцом (касается это и женщин) духовным, продолжателем во всех смыслах этого слова своего рода, народа, расы и даже всего человечества, поддерживая и сохраняя все лучшее, что в нем есть. Многие люди с правильно проработанным Иерофантом становятся духовными учителями, преподавателями, философами или лидерами социальных движений.",
            [6] = "Предназначение",
            [7] = "Предназначение",
